import { useState } from "react";
const SearchDoctor = (props) => {
    
    
    const specialityTypes=["Orthodontist","Opthalmologist","Paediatrician","Gastroenrologist","Cardiollogist"]
    const [localState, setLocalState] = useState({ speciality: ""});
    const handleChange = (e) => {
        e.preventDefault();
        setLocalState({ ...localState, [e.target.name]: e.target.value });

    };
    const searchDoctor = (e) => {
        e.preventDefault();
        props.searchDoctorBySpeciality(localState.speciality);

    };
    const [localState1,setLocalState1]=useState({removeDoctor:""})
    const handleChange1=(e)=>{
e.preventDefault();
setLocalState1({...localState1,removeDoctor:e.target.value})
    }
    const deleteDoctor=()=>{
        props.deleteDoctor(localState1.removeDoctor)
    }
    return (
        <div>
            
                <h2>Search for Doctors</h2>
                <label >Speciality : </label>
                <select name="speciality" onChange={handleChange} value={localState.value}>
                    <option value={specialityTypes[0]}>Orthodontist</option>
                    <option value={specialityTypes[1]}>Opthalmologist</option>
                    <option value={specialityTypes[2]}>Paediatrician</option>
                    <option value={specialityTypes[3]}>Gastroentrologist</option>
                    <option value={specialityTypes[4]}>Cardiologist</option>
                </select>&nbsp;
<button onClick={(e)=>{props.searchDoctorBySpeciality("speciality")}} >submit</button>
<table style={{width:"100",border:"2px solid brown"}}>
    <tr style={{backgroundColor:"black",color:"white"}}>
        <td>Doctor Number</td>
        <td>Name</td>
        <td>Quallification</td>
        <td>Speciality</td>
        <td>Edit</td>
        <td>Delete</td>

    </tr>
   {props.doctorData && props.doctorData.doctorSearchResults ? props.doctorData.doctorSearchResults.map((item,key)=><tr><td>{item.doctorNumber}</td><td>{item.name}</td>
   <td>{item.qualification}</td><td>{item.speciality}</td></tr>):""}
   
</table>
            

        </div>
    );
}

export default SearchDoctor
