import {useState} from 'react'
function AddDoctor(player) {
    const [localState, setLocalState] = useState({doctorNumber:"",name:"",qualification:"",speciality:"" });

    const handleChange = (e) => {
        e.preventDefault();
        setLocalState({ ...localState, [e.target.name]: e.target.value });

    };

    const addDoctor = (e) => {
        e.preventDefault();
        props.addDoctor(localState.useState);
    }

    return (
        <div>Hello Welcome to doctors page
            <form style={{ border: "1px solid blue ", textAlign: "center" }}>
                <h3>Fill the details to add Doctor</h3>
                <label>Doctor Number:<input type="text" name="doctorNumber" value={handleChange}/></label><br />
                <label>Name:<input type="text" name="name" value={handleChange}/></label><br />
                <label>Qualification:<input type="text" name="qualification"value={handleChange}/></label><br />
                <label>Speciality:<input type="text" name="speciality"value={handleChange}/></label><br />
                <button onClick={handleChange} value={addDoctor}>Submit</button>
                {props.doctorData.addDoctorSuccess}
            </form> </div>
    );
}
export default AddDoctor;
