import {useState} from 'react'
const AddDoctor =(props)=>  {
    const [localState, setLocalState] = useState({doctorNumber:"",name:"",qualification:"",speciality:"" });

    const handleChange = (e) => {
        e.preventDefault();
        setLocalState({ ...localState, [e.target.name]: e.target.value });

    };

    const addDoctor = (e) => {
        e.preventDefault();
        props.addDoctor(localState);
    }

    return (
        <div>Hello Welcome to doctors page
            <form style={{ border: "1px solid blue ", textAlign: "center" }}>
                <h3>Fill the details to add Doctor</h3>
                <label>Doctor Number:<input type="text" name="doctorNumber" value={localState.doctorNumber} onClick={handleChange}/></label><br />
                <label>Name:<input type="text" name="name" value={localState.name} onClick={handleChange}/></label><br />
                <label>Qualification:<input type="text" name="qualification"value={localState.qualification} onClick={handleChange}/></label><br />
                <label>Speciality:<input type="text" name="speciality"value={localState.speciality} onClick={handleChange}/></label><br />
                <button onClick={addDoctor} >Submit</button>
                
            </form> </div>
    );
}
export default AddDoctor;
