import { useState } from "react";
const SearchDoctor = (props) => {
    
    
    const speciality=["Orthodontist","Opthalmologist","Paediatrician","Gastroenrologist","Cardiollogist"]
    const [localState, setLocalState] = useState({ speciality: ""});

    const searchDoctor = (e) => {
        e.preventDefault();
        setLocalState({ ...localState, [e.target.speciality]: e.target.value });

    };
    return (
        <div>
            <form>
                <h2>Search for Doctors</h2>
                <label >Speciality : </label>
                <select name="speciality" id="speciality">
                    <option value={speciality[0]}>Orthodontist</option>
                    <option value={speciality[1]}>Opthalmologist</option>
                    <option value={speciality[2]}>Paediatrician</option>
                    <option value={speciality[3]}>Gastroentrologist</option>
                    <option value={speciality[4]}>Cardiologist</option>
                </select>&nbsp;
<button onClick={searchDoctor}>Submit</button>
<table style={{width:"100",border:"2px solid brown"}}>
    <tr style={{backgroundColor:"black",color:"white"}}>
        <td>Doctor Name</td>
        <td>Name</td>
        <td>Quallification</td>
        <td>Speciality</td>
    </tr>
   <tr>{props.doctorData.SearchDoctor}</tr>
</table>
            </form>

        </div>
    );
}

export default SearchDoctor
